from typing import List

class Solution:
    
    def RadixSort(self, nums, p) -> List[int]:
        digits={ d:[] for d in "0123456789" }
        for n in nums:
            digits[n[0][p]].append(n)
        result=[]
        for d in "0123456789":
            result.extend(digits[d])
        return result
    
    def smallestTrimmedNumbers(self, nums: List[str], queries: List[List[int]]) -> List[int]:
        size=len(nums[0])
        lookupTable=[]
        tmp=[(n,i) for i,n in enumerate(nums)]
        for i in range(size-1,-1,-1):
            lookupTable.append(self.RadixSort(tmp, i))
            tmp=lookupTable[-1]
        result=[]
        for q in queries:
            result.append((lookupTable[q[1]-1][q[0]-1])[1])
        return result

sln=Solution()

assert [2,2,1,0]==sln.smallestTrimmedNumbers(nums = ["102","473","251","814"], queries = [[1,1],[2,3],[4,2],[1,2]])

